
public class LoopReverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//***
		//32
		//*
		for(int i=1;i<5;i++) {
			for(int j=1;j<=i;j++) {
				if(i%2==0) {
					System.out.print(j);
				}
				else {
					System.out.print("*");					
				}
				
			}System.out.println();
		
			
		}
		
		for(int m=4;m>=0;m--);
		{
			for(int n=4;n<=3;n--) {
				System.out.println("*");
			}
		}

}
}
